@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  :root {
    --background: 10 15% 6%;
    --foreground: 40 15% 95%;

    --card: 10 15% 10%;
    --card-foreground: 40 15% 95%;

    --popover: 10 15% 10%;
    --popover-foreground: 40 15% 95%;

    --primary: 38 92% 50%;
    --primary-foreground: 10 15% 6%;

    --secondary: 10 15% 15%;
    --secondary-foreground: 40 15% 95%;

    --muted: 10 15% 20%;
    --muted-foreground: 40 10% 60%;

    --accent: 38 92% 50%;
    --accent-foreground: 10 15% 6%;

    --destructive: 0 72% 51%;
    --destructive-foreground: 40 15% 95%;

    --success: 142 71% 45%;
    --success-foreground: 10 15% 6%;

    --warning: 38 92% 50%;
    --warning-foreground: 10 15% 6%;

    --danger: 0 72% 51%;
    --danger-foreground: 40 15% 95%;

    --border: 10 15% 20%;
    --input: 10 15% 15%;
    --ring: 38 92% 50%;

    --radius: 0.75rem;

    /* Custom */
    --glow-primary: 0 0 60px hsl(38 92% 50% / 0.3);
    --glow-success: 0 0 60px hsl(142 71% 45% / 0.3);
    --glow-danger: 0 0 60px hsl(0 72% 51% / 0.3);
  }
}

@layer utilities {
  .glow-primary {
    box-shadow: var(--glow-primary);
  }

  .glow-success {
    box-shadow: var(--glow-success);
  }

  .glow-danger {
    box-shadow: var(--glow-danger);
  }

  .text-gradient {
    background: linear-gradient(135deg, hsl(var(--primary)), hsl(38 92% 70%));
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }
}

@keyframes pulse-glow {
  0%, 100% {
    box-shadow: 0 0 20px hsl(38 92% 50% / 0.4);
  }
  50% {
    box-shadow: 0 0 40px hsl(38 92% 50% / 0.6);
  }
}

@keyframes heartbeat {
  0%, 100% {
    transform: scale(1);
  }
  14% {
    transform: scale(1.05);
  }
  28% {
    transform: scale(1);
  }
  42% {
    transform: scale(1.05);
  }
  70% {
    transform: scale(1);
  }
}

.animate-pulse-glow {
  animation: pulse-glow 2s ease-in-out infinite;
}

.animate-heartbeat {
  animation: heartbeat 1.5s ease-in-out infinite;
}

body {
  background-color: hsl(var(--background));
  color: hsl(var(--foreground));
}

