import { useState, useEffect, useCallback } from 'react';

export interface CheckInRecord {
  id: string;
  timestamp: number;
}

export interface EmergencyContact {
  name: string;
  email: string;
}

interface CheckInData {
  records: CheckInRecord[];
  emergencyContact: EmergencyContact | null;
  lastCheckIn: number | null;
}

const STORAGE_KEY = 'dead-yet-data';
const TWO_DAYS_MS = 2 * 24 * 60 * 60 * 1000;

export function useCheckIn() {
  const [data, setData] = useState<CheckInData>(() => {
    const stored = localStorage.getItem(STORAGE_KEY);
    if (stored) {
      return JSON.parse(stored);
    }
    return {
      records: [],
      emergencyContact: null,
      lastCheckIn: null,
    };
  });

  const [timeRemaining, setTimeRemaining] = useState<number>(TWO_DAYS_MS);

  useEffect(() => {
    localStorage.setItem(STORAGE_KEY, JSON.stringify(data));
  }, [data]);

  useEffect(() => {
    const updateTimeRemaining = () => {
      if (data.lastCheckIn) {
        const elapsed = Date.now() - data.lastCheckIn;
        const remaining = Math.max(0, TWO_DAYS_MS - elapsed);
        setTimeRemaining(remaining);
      } else {
        setTimeRemaining(TWO_DAYS_MS);
      }
    };

    updateTimeRemaining();
    const interval = setInterval(updateTimeRemaining, 1000);
    return () => clearInterval(interval);
  }, [data.lastCheckIn]);

  const checkIn = useCallback(() => {
    const now = Date.now();
    const newRecord: CheckInRecord = {
      id: crypto.randomUUID(),
      timestamp: now,
    };

    setData((prev) => ({
      ...prev,
      records: [newRecord, ...prev.records].slice(0, 30),
      lastCheckIn: now,
    }));
  }, []);

  const setEmergencyContact = useCallback((contact: EmergencyContact | null) => {
    setData((prev) => ({
      ...prev,
      emergencyContact: contact,
    }));
  }, []);

  const isOverdue = timeRemaining === 0 && data.lastCheckIn !== null;
  const hasCheckedInToday = data.lastCheckIn
    ? Date.now() - data.lastCheckIn < 24 * 60 * 60 * 1000
    : false;

  return {
    records: data.records,
    emergencyContact: data.emergencyContact,
    lastCheckIn: data.lastCheckIn,
    timeRemaining,
    isOverdue,
    hasCheckedInToday,
    checkIn,
    setEmergencyContact,
  };
}

import { Heart } from 'lucide-react';

interface CheckInButtonProps {
  onCheckIn: () => void;
  hasCheckedInToday: boolean;
  isOverdue: boolean;
}

export function CheckInButton({ onCheckIn, hasCheckedInToday, isOverdue }: CheckInButtonProps) {
  const getButtonStyles = () => {
    if (isOverdue) {
      return 'bg-danger glow-danger';
    }
    if (hasCheckedInToday) {
      return 'bg-success glow-success';
    }
    return 'bg-primary animate-pulse-glow';
  };

  const getStatusText = () => {
    if (isOverdue) {
      return '已超时！';
    }
    if (hasCheckedInToday) {
      return '今日已签到';
    }
    return '点击签到';
  };

  return (
    <div className="flex flex-col items-center gap-6">
      <button
        onClick={onCheckIn}
        className={`
          relative w-48 h-48 rounded-full
          flex items-center justify-center
          transition-all duration-300 ease-out
          hover:scale-105 active:scale-95
          ${getButtonStyles()}
        `}
      >
        <Heart
          className={`w-20 h-20 text-primary-foreground ${hasCheckedInToday ? 'animate-heartbeat' : ''}`}
          fill="currentColor"
        />
      </button>
      <p className="text-xl font-medium text-foreground">{getStatusText()}</p>
    </div>
  );
}

interface CountdownTimerProps {
  timeRemaining: number;
  isOverdue: boolean;
}

export function CountdownTimer({ timeRemaining, isOverdue }: CountdownTimerProps) {
  const formatTime = (ms: number) => {
    const totalSeconds = Math.floor(ms / 1000);
    const hours = Math.floor(totalSeconds / 3600);
    const minutes = Math.floor((totalSeconds % 3600) / 60);
    const seconds = totalSeconds % 60;

    return {
      hours: hours.toString().padStart(2, '0'),
      minutes: minutes.toString().padStart(2, '0'),
      seconds: seconds.toString().padStart(2, '0'),
    };
  };

  const { hours, minutes, seconds } = formatTime(timeRemaining);

  const getTimerColor = () => {
    if (isOverdue) return 'text-danger';
    const hoursLeft = timeRemaining / (1000 * 60 * 60);
    if (hoursLeft < 12) return 'text-danger';
    if (hoursLeft < 24) return 'text-warning';
    return 'text-success';
  };

  return (
    <div className="text-center">
      <p className="text-muted-foreground mb-2">距离触发通知</p>
      <div className={`font-mono text-5xl font-bold ${getTimerColor()}`}>
        {isOverdue ? (
          <span className="animate-pulse">已超时</span>
        ) : (
          <>
            <span>{hours}</span>
            <span className="mx-1 animate-pulse">:</span>
            <span>{minutes}</span>
            <span className="mx-1 animate-pulse">:</span>
            <span>{seconds}</span>
          </>
        )}
      </div>
      {!isOverdue && (
        <p className="text-muted-foreground mt-2 text-sm">
          {parseInt(hours)}小时 {parseInt(minutes)}分钟 {parseInt(seconds)}秒
        </p>
      )}
    </div>
  );
}

import { useState } from 'react';
import { UserCircle, Mail, Save, X, Edit2 } from 'lucide-react';
import type { EmergencyContact } from '../hooks/useCheckIn';

interface EmergencyContactFormProps {
  contact: EmergencyContact | null;
  onSave: (contact: EmergencyContact | null) => void;
}

export function EmergencyContactForm({ contact, onSave }: EmergencyContactFormProps) {
  const [isEditing, setIsEditing] = useState(false);
  const [name, setName] = useState(contact?.name || '');
  const [email, setEmail] = useState(contact?.email || '');

  const handleSave = () => {
    if (name.trim() && email.trim()) {
      onSave({ name: name.trim(), email: email.trim() });
      setIsEditing(false);
    }
  };

  const handleCancel = () => {
    setName(contact?.name || '');
    setEmail(contact?.email || '');
    setIsEditing(false);
  };

  const handleDelete = () => {
    onSave(null);
    setName('');
    setEmail('');
    setIsEditing(false);
  };

  if (!isEditing && contact) {
    return (
      <div className="bg-card rounded-xl p-6 border border-border">
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-lg font-semibold text-foreground">紧急联系人</h3>
          <button
            onClick={() => setIsEditing(true)}
            className="text-muted-foreground hover:text-primary transition-colors"
          >
            <Edit2 className="w-5 h-5" />
          </button>
        </div>
        <div className="space-y-3">
          <div className="flex items-center gap-3">
            <UserCircle className="w-5 h-5 text-primary" />
            <span className="text-foreground">{contact.name}</span>
          </div>
          <div className="flex items-center gap-3">
            <Mail className="w-5 h-5 text-primary" />
            <span className="text-foreground">{contact.email}</span>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="bg-card rounded-xl p-6 border border-border">
      <h3 className="text-lg font-semibold text-foreground mb-4">
        {contact ? '编辑紧急联系人' : '设置紧急联系人'}
      </h3>
      <div className="space-y-4">
        <div>
          <label className="block text-sm text-muted-foreground mb-2">姓名</label>
          <div className="relative">
            <UserCircle className="absolute left-3 top-1/2 -translate-y-1/2 w-5 h-5 text-muted-foreground" />
            <input
              type="text"
              value={name}
              onChange={(e) => setName(e.target.value)}
              placeholder="输入联系人姓名"
              className="w-full bg-input border border-border rounded-lg py-3 pl-11 pr-4 text-foreground placeholder:text-muted-foreground focus:outline-none focus:ring-2 focus:ring-primary"
            />
          </div>
        </div>
        <div>
          <label className="block text-sm text-muted-foreground mb-2">邮箱</label>
          <div className="relative">
            <Mail className="absolute left-3 top-1/2 -translate-y-1/2 w-5 h-5 text-muted-foreground" />
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="输入联系人邮箱"
              className="w-full bg-input border border-border rounded-lg py-3 pl-11 pr-4 text-foreground placeholder:text-muted-foreground focus:outline-none focus:ring-2 focus:ring-primary"
            />
          </div>
        </div>
        <div className="flex gap-3 pt-2">
          <button
            onClick={handleSave}
            disabled={!name.trim() || !email.trim()}
            className="flex-1 bg-primary text-primary-foreground py-3 rounded-lg font-medium flex items-center justify-center gap-2 hover:opacity-90 transition-opacity disabled:opacity-50 disabled:cursor-not-allowed"
          >
            <Save className="w-5 h-5" />
            保存
          </button>
          {contact && (
            <>
              <button
                onClick={handleCancel}
                className="flex-1 bg-secondary text-secondary-foreground py-3 rounded-lg font-medium flex items-center justify-center gap-2 hover:opacity-90 transition-opacity"
              >
                <X className="w-5 h-5" />
                取消
              </button>
              <button
                onClick={handleDelete}
                className="bg-danger text-danger-foreground py-3 px-4 rounded-lg font-medium hover:opacity-90 transition-opacity"
              >
                删除
              </button>
            </>
          )}
        </div>
      </div>
    </div>
  );
}

import { Clock, CheckCircle } from 'lucide-react';
import type { CheckInRecord } from '../hooks/useCheckIn';

interface CheckInHistoryProps {
  records: CheckInRecord[];
}

export function CheckInHistory({ records }: CheckInHistoryProps) {
  const formatDate = (timestamp: number) => {
    const date = new Date(timestamp);
    return date.toLocaleDateString('zh-CN', {
      month: 'short',
      day: 'numeric',
      weekday: 'short',
    });
  };

  const formatTime = (timestamp: number) => {
    const date = new Date(timestamp);
    return date.toLocaleTimeString('zh-CN', {
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  if (records.length === 0) {
    return (
      <div className="bg-card rounded-xl p-6 border border-border">
        <h3 className="text-lg font-semibold text-foreground mb-4">签到历史</h3>
        <div className="text-center py-8">
          <Clock className="w-12 h-12 text-muted-foreground mx-auto mb-3" />
          <p className="text-muted-foreground">暂无签到记录</p>
        </div>
      </div>
    );
  }

  return (
    <div className="bg-card rounded-xl p-6 border border-border">
      <h3 className="text-lg font-semibold text-foreground mb-4">签到历史</h3>
      <div className="space-y-3 max-h-80 overflow-y-auto">
        {records.map((record, index) => (
          <div
            key={record.id}
            className={`flex items-center justify-between p-3 rounded-lg ${
              index === 0 ? 'bg-primary/10 border border-primary/30' : 'bg-secondary'
            }`}
          >
            <div className="flex items-center gap-3">
              <CheckCircle
                className={`w-5 h-5 ${index === 0 ? 'text-primary' : 'text-success'}`}
              />
              <span className="text-foreground">{formatDate(record.timestamp)}</span>
            </div>
            <span className="text-muted-foreground text-sm">
              {formatTime(record.timestamp)}
            </span>
          </div>
        ))}
      </div>
    </div>
  );
}

import { Heart } from 'lucide-react';
import { CheckInButton } from '../components/CheckInButton';
import { CountdownTimer } from '../components/CountdownTimer';
import { EmergencyContactForm } from '../components/EmergencyContactForm';
import { CheckInHistory } from '../components/CheckInHistory';
import { useCheckIn } from '../hooks/useCheckIn';

export default function Index() {
  const {
    records,
    emergencyContact,
    lastCheckIn,
    timeRemaining,
    isOverdue,
    hasCheckedInToday,
    checkIn,
    setEmergencyContact,
  } = useCheckIn();

  const formatLastCheckIn = () => {
    if (!lastCheckIn) return '从未签到';
    const date = new Date(lastCheckIn);
    return date.toLocaleString('zh-CN', {
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  return (
    <div className="min-h-screen bg-background">
      {/* Header */}
      <header className="border-b border-border">
        <div className="container mx-auto px-4 py-6">
          <div className="flex items-center justify-center gap-3">
            <Heart className="w-8 h-8 text-primary" fill="currentColor" />
            <h1 className="text-2xl font-bold text-gradient">死了么</h1>
          </div>
          <p className="text-center text-muted-foreground mt-2">
            每日签到，让关心你的人安心
          </p>
        </div>
      </header>

      <main className="container mx-auto px-4 py-8">
        <div className="max-w-xl mx-auto space-y-8">
          {/* Check-in Section */}
          <section className="text-center py-8">
            <CheckInButton
              onCheckIn={checkIn}
              hasCheckedInToday={hasCheckedInToday}
              isOverdue={isOverdue}
            />
            <p className="text-muted-foreground mt-4">
              上次签到: {formatLastCheckIn()}
            </p>
          </section>

          {/* Countdown Timer */}
          <section className="bg-card rounded-xl p-6 border border-border">
            <CountdownTimer timeRemaining={timeRemaining} isOverdue={isOverdue} />
          </section>

          {/* Warning Message when overdue */}
          {isOverdue && emergencyContact && (
            <section className="bg-danger/10 border border-danger/30 rounded-xl p-6">
              <div className="text-center">
                <p className="text-danger font-semibold text-lg">
                  警告：已超过48小时未签到！
                </p>
                <p className="text-danger/80 mt-2">
                  系统将通知紧急联系人 {emergencyContact.name}
                </p>
              </div>
            </section>
          )}

          {/* Emergency Contact */}
          <section>
            <EmergencyContactForm
              contact={emergencyContact}
              onSave={setEmergencyContact}
            />
          </section>

          {/* Check-in History */}
          <section>
            <CheckInHistory records={records} />
          </section>
        </div>
      </main>

      {/* Footer */}
      <footer className="border-t border-border mt-12">
        <div className="container mx-auto px-4 py-6">
          <p className="text-center text-muted-foreground text-sm">
            记得每天签到，让爱你的人知道你安好
          </p>
        </div>
      </footer>
    </div>
  );
}

